import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.for-each.js";
import '../assets/admin.css';
import axios from 'axios';
import { ref, onMounted, watch } from 'vue';
export default {
  setup() {
    // State Variables
    const products = ref([]);
    const tableRef = ref(null);
    const baseUrl = 'http://localhost:5555';

    // Form states
    const newProduct = ref({
      name: '',
      description: '',
      price: '',
      image_url: ''
    });
    const editProduct = ref({
      id: 0,
      name: '',
      description: '',
      price: '',
      quantity: 0,
      image_url: ''
    });

    // Modal visibility states
    const showAddProductModal = ref(false);
    const showAddQuantityModal = ref(false);
    const showEditProductModal = ref(false);

    // Selected product and quantity to add
    const selectedProduct = ref(null);
    const quantityToAdd = ref(null);

    // Snackbar state for messages
    const snackbar = ref({
      show: false,
      message: '',
      color: 'success'
    });

    // Voucher management
    const activeTab = ref('products');
    const vouchers = ref([]);
    const newVoucher = ref({
      code: '',
      discount_type: 'percentage',
      discount_amount: '',
      min_purchase: '',
      expiry_date: ''
    });
    const voucherModal = ref(null);

    // Methods
    const fetchProducts = async () => {
      try {
        const response = await axios.get(`${baseUrl}/products`);
        products.value = Array.isArray(response.data.products) ? response.data.products : [];
      } catch (error) {
        console.error('Error fetching products:', error);
        products.value = [];
      }
    };
    const openEditProductModal = item => {
      editProduct.value = {
        ...item
      };
      showEditProductModal.value = true;
    };
    const updateProduct = async () => {
      console.log("Product to Update:", editProduct.value); // Debugging log for product data

      if (!editProduct.value || !editProduct.value.id) {
        handleError("Product ID is missing. Cannot update the product.");
        return;
      }
      try {
        const formData = new FormData();
        if (editProduct.value.name) formData.append("name", editProduct.value.name);
        if (editProduct.value.description) formData.append("description", editProduct.value.description);
        if (editProduct.value.price) formData.append("price", editProduct.value.price);
        if (editProduct.value.image_url instanceof File) formData.append("image", editProduct.value.image_url);

        // Make the PUT request
        const response = await axios.put(`${baseUrl}/products/${editProduct.value.id}`, formData, {
          headers: {
            "Content-Type": "multipart/form-data"
          }
        });
        if (response.status === 200 && response.data.success) {
          const successMessage = response.data.message || "Product updated successfully!";
          handleResponse(response, successMessage);
        } else {
          // Handle unexpected responses
          const errorMessage = response.data && response.data.message ? response.data.message : "Unexpected response from the server.";
          handleError(errorMessage);
        }
      } catch (error) {
        console.error("Error updating product:", error);
        const errorMessage = error.response && error.response.data && error.response.data.message ? error.response.data.message : "Failed to update product. Please try again.";
        handleError(errorMessage);
      } finally {
        // Ensure the modal is closed after submission
        const modalElement = document.getElementById("editProductModal");
        if (modalElement) {
          const modalInstance = bootstrap.Modal.getInstance(modalElement);
          if (modalInstance) modalInstance.hide();
        }
        // Reset the `showEditProductModal` state (if necessary)
        showEditProductModal.value = false;
      }
    };
    const addProduct = async () => {
      console.log('Product to Add:', newProduct.value); // Debugging log for product data

      const formData = new FormData();
      formData.append('name', newProduct.value.name);
      formData.append('description', newProduct.value.description);
      formData.append('price', newProduct.value.price);
      formData.append('image', newProduct.value.image_url);
      try {
        // Make the POST request
        const response = await axios.post(`${baseUrl}/add-product`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        const successMessage = response.data.message || 'Product added successfully!';
        handleResponse(response, successMessage);

        // Reset the form and clear the file input
        resetNewProductForm();
      } catch (error) {
        // Improved error handling to show specific error messages
        console.error('Error adding product:', error);
        const errorMessage = error.response && error.response.data && error.response.data.message ? error.response.data.message : 'An error occurred while adding the product. Please try again.';
        handleError(errorMessage);
      } finally {
        // Ensure the modal is closed after submission
        const modalElement = document.getElementById('addProductModal');
        if (modalElement) {
          const modalInstance = bootstrap.Modal.getInstance(modalElement);
          if (modalInstance) modalInstance.hide();
        }

        // Reset the `showAddProductModal` state (if needed)
        showAddProductModal.value = false;

        // Explicitly reset the file input field
        resetFileInput();
      }
    };
    // Helper function to reset the file input
    const resetFileInput = () => {
      const fileInput = document.getElementById('productImage');
      if (fileInput) {
        fileInput.value = ''; // Clear the file input value
      }
    };
    const openAddQuantityModal = item => {
      selectedProduct.value = item;
      quantityToAdd.value = null;
      showAddQuantityModal.value = true;
    };
    const addQuantity = async () => {
      console.log('Quantity to Add:', quantityToAdd.value); // Debugging line
      if (!selectedProduct.value || quantityToAdd.value === null) return;
      try {
        const response = await axios.patch(`${baseUrl}/products/${selectedProduct.value.id}/quantity`, {
          quantity: quantityToAdd.value
        });
        const successMessage = response.data.message || `Quantity added successfully to ${selectedProduct.value.name}!`;
        handleResponse(response, successMessage);
        const modalElement = document.getElementById('addQuantityModal');
        if (modalElement) {
          const modalInstance = bootstrap.Modal.getInstance(modalElement);
          modalInstance.hide();
        }
        selectedProduct.value = null;
      } catch (error) {
        console.error('Error adding quantity:', error);
        const errorMessage = error.response && error.response.data && error.response.data.message ? error.response.data.message : 'Error adding quantity';
        handleError(errorMessage);
        const modalElement = document.getElementById('addQuantityModal');
        if (modalElement) {
          const modalInstance = bootstrap.Modal.getInstance(modalElement);
          modalInstance.hide();
        }
        selectedProduct.value = null;
      }
    };
    const handleFileChange = (event, productType) => {
      const file = event.target.files[0];
      if (file) {
        if (productType === 'newProduct') {
          newProduct.value.image_url = file;
        } else if (productType === 'editProduct') {
          editProduct.value.image_url = file;
        }
      }
    };
    const handleLogout = async () => {
      try {
        console.log("Logging out...");
        await axios.post(`${baseUrl}/logout`, {}, {
          withCredentials: true
        });
        localStorage.removeItem("userRole");
        window.location.href = "/login"; // Redirecting to login page after logout
      } catch (error) {
        console.error("Logout failed", error);
      }
    };
    const handleResponse = (response, successMessage) => {
      if (response.data.success) {
        snackbar.value = {
          show: true,
          message: successMessage,
          color: 'success'
        };
        fetchProducts();
        setTimeout(() => {
          snackbar.value.show = false;
        }, 3000);
      } else {
        snackbar.value = {
          show: true,
          message: response.data.message || 'Operation failed',
          color: 'danger'
        };
        setTimeout(() => {
          snackbar.value.show = false;
        }, 3000);
      }
    };
    const handleError = message => {
      snackbar.value = {
        show: true,
        message,
        color: 'danger'
      };
      setTimeout(() => {
        snackbar.value.show = false;
      }, 3000);
    };
    const createFormData = productData => {
      const formData = new FormData();
      Object.keys(productData).forEach(key => {
        if (key === 'image_url' && productData[key] instanceof File) {
          formData.append('image', productData[key]);
        } else {
          formData.append(key, productData[key]);
        }
      });
      return formData;
    };
    const resetNewProductForm = () => {
      newProduct.value = {
        name: '',
        description: '',
        price: '',
        image_url: ''
      };
    };
    const loadDataTables = () => {
      const link = document.createElement('link');
      link.rel = 'stylesheet';
      link.href = 'https://cdn.datatables.net/1.13.6/css/jquery.dataTables.min.css';
      document.head.appendChild(link);
      const scriptJQuery = document.createElement('script');
      scriptJQuery.src = 'https://code.jquery.com/jquery-3.6.0.min.js';
      scriptJQuery.onload = () => initDataTable();
      document.head.appendChild(scriptJQuery);
    };
    const initDataTable = () => {
      const scriptDataTable = document.createElement('script');
      scriptDataTable.src = 'https://cdn.datatables.net/1.13.6/js/jquery.dataTables.min.js';
      scriptDataTable.onload = () => {
        if (tableRef.value) {
          $(tableRef.value).DataTable();
        }
      };
      document.head.appendChild(scriptDataTable);
    };
    const fetchVouchers = async () => {
      try {
        const response = await axios.get('http://localhost:5555/admin/vouchers', {
          withCredentials: true
        });
        vouchers.value = response.data.vouchers;
      } catch (error) {
        console.error('Error fetching vouchers:', error);
        handleError('Failed to fetch vouchers');
      }
    };
    const showAddVoucherModal = () => {
      if (!voucherModal.value) {
        voucherModal.value = new bootstrap.Modal(document.getElementById('addVoucherModal'));
      }
      resetVoucherForm();
      voucherModal.value.show();
    };
    const resetVoucherForm = () => {
      newVoucher.value = {
        code: '',
        discount_type: 'percentage',
        discount_amount: '',
        min_purchase: '',
        expiry_date: ''
      };
    };
    const submitVoucher = async () => {
      try {
        await axios.post('http://localhost:5555/admin/vouchers', newVoucher.value, {
          withCredentials: true
        });
        voucherModal.value.hide();
        handleResponse({
          data: {
            success: true
          }
        }, 'Voucher created successfully');
        fetchVouchers();
      } catch (error) {
        console.error('Error creating voucher:', error);
        handleError('Failed to create voucher');
      }
    };
    const toggleVoucherStatus = async voucher => {
      try {
        const newStatus = voucher.status === 'active' ? 'inactive' : 'active';
        await axios.put('http://localhost:5555/admin/vouchers/status', {
          id: voucher.id,
          status: newStatus
        }, {
          withCredentials: true
        });
        voucher.status = newStatus;
        handleResponse({
          data: {
            success: true
          }
        }, 'Voucher status updated successfully');
      } catch (error) {
        console.error('Error updating voucher status:', error);
        handleError('Failed to update voucher status');
      }
    };
    const getStatusBadgeClass = status => {
      return {
        'active': 'bg-success',
        'inactive': 'bg-danger'
      }[status] || 'bg-secondary';
    };
    onMounted(() => {
      fetchProducts();
      loadDataTables();
    });
    watch(activeTab, newTab => {
      if (newTab === 'vouchers') {
        fetchVouchers();
      }
    });
    return {
      products,
      tableRef,
      baseUrl,
      newProduct,
      showAddProductModal,
      showAddQuantityModal,
      selectedProduct,
      quantityToAdd,
      snackbar,
      showEditProductModal,
      editProduct,
      openEditProductModal,
      updateProduct,
      addProduct,
      openAddQuantityModal,
      addQuantity,
      handleFileChange,
      handleLogout,
      activeTab,
      vouchers,
      newVoucher,
      showAddVoucherModal,
      submitVoucher,
      toggleVoucherStatus
    };
  }
};
